mapper.select:
SELECT 
FROM custom_service_orders

     AND enterprise_id = #{enterpriseId}
     AND staff_id = #{staffId}
     AND service_status = #{serviceStatus}
     AND current_stage = #{currentStage}
     AND settle_status = #{settleStatus}

ORDER BY id DESC

    LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM custom_service_orders

     AND enterprise_id = #{enterpriseId}
     AND staff_id = #{staffId}
     AND service_status = #{serviceStatus}
     AND current_stage = #{currentStage}
     AND settle_status = #{settleStatus}

mapper.update:
UPDATE custom_service_orders
enterprise_id = #{enterpriseId}, intention_id = #{intentionId}, staff_id = #{staffId}, current_stage = #{currentStage}, service_status = #{serviceStatus}, loan_amount = #{loanAmount}, commission_amount = #{commissionAmount}, service_cost = #{serviceCost}, cost_details = #{costDetails}, settle_status = #{settleStatus},
WHERE id = #{id}
