mapper.select:
SELECT 
FROM enterprises

     AND match_status = #{matchStatus}
     AND industry = #{industry}
     AND region_code = #{regionCode}

ORDER BY id DESC

    LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM enterprises

     AND match_status = #{matchStatus}
     AND industry = #{industry}
     AND region_code = #{regionCode}

mapper.update:
UPDATE enterprises
full_name = #{fullName}, credit_code = #{creditCode}, industry = #{industry}, tax_rating = #{taxRating}, region_code = #{regionCode}, annual_turnover = #{annualTurnover}, annual_tax_amount = #{annualTaxAmount}, existing_loan_balance = #{existingLoanBalance}, match_status = #{matchStatus}, profile_data = #{profileData},
WHERE id = #{id}
