mapper.select:
SELECT 
FROM financing_intentions

     AND enterprise_id = #{enterpriseId}
     AND user_id = #{userId}
     AND status = #{status}

ORDER BY id DESC

    LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM financing_intentions

     AND enterprise_id = #{enterpriseId}
     AND user_id = #{userId}
     AND status = #{status}

mapper.update:
UPDATE financing_intentions
enterprise_id = #{enterpriseId}, user_id = #{userId}, expected_amount = #{expectedAmount}, expected_term = #{expectedTerm}, purpose = #{purpose}, repayment_source = #{repaymentSource}, guarantee_type = #{guaranteeType}, target_product_id = #{targetProductId}, contact_mobile = #{contactMobile}, status = #{status}, refusal_reason = #{refusalReason}, urgency_level = #{urgencyLevel},
WHERE id = #{id}

UPDATE financing_intentions
SET status = #{status}, refusal_reason = #{refusalReason}
WHERE id = #{id}

UPDATE financing_intentions
SET target_product_id = #{targetProductId}
WHERE id = #{id}
